<script setup>
import { reactive, defineEmits } from 'vue';
let emits = defineEmits('update-tab')

let obj = reactive({
  tab1: true,
  tab2: false,
  tab3: false,
  tab4: false,
  tab5: false,
  tab6: false
})

let obj1 = reactive({
  'tab1': '4', // 综合
  'tab2': '2', // 前端
  'tab3': '1', // 后端
  'tab4': '3', // 大数据
  'tab5': '0', // ios
})

const mouseOver = (index) => {
  Object.keys(obj).forEach(item => {
    // obj1.item
    if (item === index) {
      obj[item] = true
      emits('update-tab', obj1[item])
    } else {
      obj[item] = false
    }
  })
}
